mapper.select:
WITH P AS( SELECT M.mtid , M.sido , M.mt_date , M.category , M.nm_ppl , M.title , M.CRTR_ID , M.created_date , COUNT(L.mtid) AS LCNT , COUNT(P.mtid) AS PCNT FROM MEET_INFO M LEFT JOIN ( SELECT DISTINCT MTID FROM MEET_LIKE ) L ON M.mtid = L.mtid LEFT JOIN MEET_PRTCP P ON M.mtid = P.mtid
WHERE M.ED_DATE >= ( SELECT TO_CHAR(SYSDATE,'YYYY-MM-DD') FROM DUAL )
AND LOWER(M.TITLE) LIKE LOWER('%' || #{searchTitle} || '%')
AND M.CATEGORY = #{searchCategory}
AND M.SIDO = #{searchSidoValue}
AND M.NM_PPL > ( SELECT COUNT(*) FROM MEET_PRTCP P1 WHERE P1.MTID = M.MTID )
GROUP BY M.MTID, M.SIDO, M.MT_DATE, M.CATEGORY, M.NM_PPL, M.TITLE, M.CREATED_DATE, M.CRTR_ID
ORDER BY M.CREATED_DATE DESC
ORDER BY LCNT DESC
) SELECT * FROM P OFFSET #{pageNum} ROWS FETCH NEXT #{amount} ROWS ONLY

SELECT img_1
FROM MEET_INFO
where mtid = #{ mtid }
