mapper.insert:
INSERT INTO USER_INFO (
	  ID
	, PWD
	, NAME
	, GENDER
	, BIRTH
	, PHONE
	, EMAIL
	, JOIN_DATE
	, DIVISION
) VALUES (
	  #{id}
	, #{pwd}
	, #{name}
	, #{gender}
	, #{birth}
	, #{phone}
	, #{email}
	, systimestamp
	, 'M'
)

mapper.select:
SELECT COUNT(*) AS TOCNT
FROM USER_INFO
WHERE ID = #{id}

