mapper.insert:
INSERT INTO USER_INFO (
	  ID
	, PWD
	, NAME
	, GENDER
	, BIRTH
	, PHONE
	, EMAIL
	, JOIN_DATE
	, DIVISION
) VALUES (
	  #{id}
	, #{pwd}
	, #{name}
	, #{gender}
	, #{birth}
	, #{phone}
	, #{email}
	, systimestamp
	, 'ROLE_M'
)

mapper.select:
SELECT COUNT(*) AS TOCNT
FROM USER_INFO
WHERE ID = #{id}

SELECT
      UI.ID
FROM USER_INFO UI
WHERE UI.NAME = #{name}
AND UI.EMAIL = #{email}

SELECT
    COUNT(*)
FROM USER_INFO UI
WHERE UI.NAME = #{name}
AND UI.EMAIL = #{email}

	AND UI.ID = #{id}

mapper.update:
UPDATE USER_INFO UI
SET UI.PWD = #{pwd}
WHERE UI.ID = #{id}

		UPDATE user_info
        SET pwd = #{inputPwd}
        where id = #{id}

UPDATE USER_INFO
SET PROFILE = #{profile}
