mapper.insert:
INSERT INTO USER_INFO (
	  ID
	, PWD
	, NAME
	, GENDER
	, BIRTH
	, PHONE
	, EMAIL
	, JOIN_DATE
	, DIVISION
) VALUES (
	  #{id}
	, #{pwd}
	, #{name}
	, #{gender}
	, systimestamp
	, #{phone}
	, #{email}
	, systimestamp
	, 'M'
)

mapper.update:
UPDATE USER_INFO
SET PROFILE = #{profile}
WHERE id = #{id}

mapper.select:
select * from POST_COMMENT
where PID = #{pid}
order by MODIFIED_DATE desc

mapper.update:
update POST_COMMENT
set CONTENT = #{content}, MODIFIED_DATE = systimestamp
